package java_test;

public class additionops {
	static int a;
	 static int b;
	 static int c,d;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		a=5;
		b=4;
		c=9;
		
		
		 d = a+b+c+d;
		 
		 System.out.println("Sum of all numbers ="  +d);
		 a=9;
		 d = a+b+c+d;
		 System.out.println("Sum of all numbers after reinitializing ="  +d);
		 

	}

}
